export class TestDataError extends Error {
  constructor(message: string) {
    super(message);
    this.name = TestDataError.name;
  }
}

import {
  SchemaDescription,
  SchemaFieldDescription,
  ValidationError,
} from 'yup';
import { validationSchema } from '@/pages/add-product';
